contracts/PayrollManagement.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract PayrollManagement is ReentrancyGuard {
    // Mapping để chống double-payment: employeeDid => period => đã thanh toán
    mapping(address => mapping(string => bool)) public paidRecords;

    // Struct cho PayrollRecord
    struct PayrollRecord {
        address employeeWallet;    // Địa chỉ ví nhân viên
        uint256 totalAmount;       // Tổng tiền wei
        uint256 baseSalary;        // Lương cơ bản wei
        uint256 kpiBonus;          // Thưởng KPI wei
        uint256 allowance;         // Phụ cấp wei
        uint256 deductions;        // Khấu trừ wei
        string period;             // Kỳ lương
    }

    // Event để ghi lại thanh toán
    event PaymentExecuted(address indexed employee, uint256 amount, string period, uint256 timestamp);

    // Hàm deposit để nạp tiền vào hợp đồng
    function deposit() external payable {}

    // Hàm thanh toán lương
    function paySalary(address employee, uint256 amount, string memory period) external payable nonReentrant {
        // Kiểm tra chưa thanh toán cho kỳ này
        require(!paidRecords[employee][period], "Da thanh toan cho ky nay");

        // Kiểm tra số tiền > 0
        require(amount > 0, "So tien khong hop le");

        // Kiểm tra số dư hợp đồng
        require(address(this).balance >= amount, "So du hop dong khong du");

        // Transfer tiền đến ví nhân viên
        payable(employee).transfer(amount);

        // Đánh dấu đã thanh toán
        paidRecords[employee][period] = true;

        // Emit event
        emit PaymentExecuted(employee, amount, period, block.timestamp);
    }

    // Hàm kiểm tra trạng thái thanh toán
    function isPaid(address employee, string memory period) external view returns (bool) {
        return paidRecords[employee][period];
    }

    // Hàm lấy số dư hợp đồng
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    // Fallback để nhận ETH
    receive() external payable {}
}
